function show_confirm(query,color,options){var confirm_password=document.getElementById(query);var confirm_pass_icon=document.querySelector('.fa-check');if(confirm_password.type==="password"){if(color){confirm_pass_icon.style.color=color;}else{confirm_pass_icon.style.color="#7f2092";}
confirm_password.type="text";if(options){confirm_password.style.border=options[0];confirm_password.style.width=options[1];}}else{delete color;confirm_password.type="password";if(options){confirm_password.style.width="50%";}
delete options;confirm_pass_icon.style.color="grey";}}
function show_pass(query,color,options){var password=document.getElementById(query);var pass_icon=document.querySelector('.fa-lock');if(password.type=="password"){if(color){pass_icon.style.color=color;}else{pass_icon.style.color="#7f2092";}
password.type="text";if(options){password.style.border=options[0];password.style.width=options[1];}}else{delete color;password.type="password";if(options){password.style.width="50%";}
delete options;pass_icon.style.color="grey";}}
function NoValidateInput(){var inputs=document.getElementsByTagName('input');for(var i=0;i<inputs.length;i++){if(inputs[i].type.toLowerCase()=='text'){var input=inputs[i];input.removeAttribute("required");input.setAttribute("novalidate");}}}